INSERT INTO avaliacao.Tbpointscale (id, whattasted, whentasted, whatsaw, whataromas, whatflavors, whatopinion, pointscale) VALUES ('7c5368a2-ac33-4861-9372-7b6fd1322463', 'Faustino Rivero Ulecia Reserva Rioja DOCa, safra 2018, vinho tinto, seco, 14% de álcool, produzido e engarrafado por Marqués del Atrio, em Rioja, Espanha.', 'Em 28/10/2023, às 21:30h.', 'Vermelho escuro , opaco e brilhante.', 'Compota de frutas , tostado e chocolate.', 'Taninos suaves e bastante acidez. Corpo médio e persistente.', 'Mais um ótimo tinto da Rioja que degusto, ótimos aromas de frutas negras com toque de pimenta e tabaco, ótimo corpo e estrutura com tanicidade eacidez bem equilibradas, vinho bem agradável', 'GOOD');
INSERT INTO avaliacao.Tbpointscale (id, whattasted, whentasted, whatsaw, whataromas, whatflavors, whatopinion, pointscale) VALUES('24ffe2d4-2990-4718-9e49-7f97190544b9', 'Portada Winemakers Selection, safra 2020, vinho tinto, seco, 12,5% de álcool, produzido e engarrafado por DFJ Vinhos, em Lisboa, Portugal.', 'Em 15/09/2023, às 20:00h.', 'Vinho de cor vermelho rubi, com reflexos violáceos, límpido, brilhante, com lágrimas finas, rápidas e abundantes.', 'Aromas de frutas vermelhas maduras, como cereja e framboesa, com notas de especiarias, como pimenta preta, e de ervas, como tomilho.', 'Em boca, o vinho é seco, com acidez média, taninos médios, álcool médio, corpo médio, intensidade de sabor média, com sabores de frutas vermelhas maduras, como cereja e framboesa, com notas de especiarias, como pimenta preta, e de ervas, como tomilho.', 'Vinho de boa qualidade, com boa complexidade, equilibrado, com boa intensidade de sabor, com boa persistência, com boa tipicidade, com boa harmonização, com boa relação qualidade/preço, om potencial de guarda de 3 anos.', 'VERYGOOD');
INSERT INTO avaliacao.Tbpointscale (id, whattasted, whentasted, whatsaw, whataromas, whatflavors, whatopinion, pointscale) VALUES('19590083-b478-4fee-934b-4abfa1a753c5', 'Faustino Rivero Ulecia Reserva Rioja DOCa, safra 2018, vinho tinto, seco, produzido e engarrafado por vinícola Loma Negra, em Vale Central, Chile.', 'Em 28/10/2023, às 21:30h.', 'Coloração amarelo dourado.', 'Aroma de pimentão vermelho maduro.', 'Na boca boa acidez, lembrando frutas cítricas.', 'Muito suculento com final longo.', 'OUTSTANDING');